// 初始化变量
let workTime = 25;    // 默认工作时间（分钟）
let breakTime = 5;    // 默认休息时间（分钟）
let isWorking = true; // 当前阶段状态
let timer = null;     // 计时器实例
let timeLeft = null;  // 剩余时间（秒）
let isRunning = false;// 计时器运行状态

// DOM 元素引用
const timerDisplay = document.getElementById('timer');
const modeText = document.getElementById('modeText');
const startBtn = document.getElementById('startBtn');
const stopBtn = document.getElementById('stopBtn');
const resetBtn = document.getElementById('resetBtn');
const settings = document.getElementById('settings');
const workInput = document.getElementById('workInput');
const breakInput = document.getElementById('breakInput');
const fsButton = document.getElementById('fsButton');

// 事件监听
document.addEventListener('DOMContentLoaded', initApp);
startBtn.addEventListener('click', startTimer);
stopBtn.addEventListener('click', stopTimer);
resetBtn.addEventListener('click', resetTimer);
fsButton.addEventListener('click', toggleFullscreen);
workInput.addEventListener('change', handleTimeUpdate);
breakInput.addEventListener('change', handleTimeUpdate);

/* 核心功能 */
function initApp() {
  // 初始化显示
  workInput.value = workTime;
  breakInput.value = breakTime;
  updateDisplay(workTime, 0);
}

// 时间显示格式化
function updateDisplay(minutes, seconds) {
  timerDisplay.textContent = 
    `${String(minutes).padStart(2, '0')}:${String(seconds).padStart(2, '0')}`;
}

// 启动计时器
function startTimer() {
  if (!isRunning) {
    isRunning = true;
    settings.classList.add('hidden');
    toggleButtonStates(true);
    
    // 初始化或继续剩余时间
    if (timeLeft === null) {
      timeLeft = (isWorking ? workTime : breakTime) * 60;
    }

    timer = setInterval(() => {
      timeLeft--;
      updateDisplay(Math.floor(timeLeft / 60), timeLeft % 60);

      if (timeLeft <= 0) {
        handlePhaseEnd();
      }
    }, 1000);
  }
}

// 处理阶段结束
function handlePhaseEnd() {
  clearInterval(timer);
  playAlert();
  
  // 切换模式
  isWorking = !isWorking;
  modeText.textContent = isWorking ? '工作模式' : '休息时间';
  
  // 准备下一阶段
  timeLeft = (isWorking ? workTime : breakTime) * 60;
  isRunning = false;
  
  // 自动开始下一阶段
  startTimer();
}

// 暂停计时器
function stopTimer() {
  clearInterval(timer);
  isRunning = false;
  toggleButtonStates(false);
}

// 重置计时器
function resetTimer() {
  clearInterval(timer);
  isWorking = true;
  timeLeft = null;
  isRunning = false;
  updateDisplay(workTime, 0);
  modeText.textContent = '工作模式';
  settings.classList.remove('hidden');
  toggleButtonStates(false);
}

// 更新时间设置
function handleTimeUpdate() {
  workTime = Math.max(1, parseInt(workInput.value) || 25);
  breakTime = Math.max(1, parseInt(breakInput.value) || 5);
  resetTimer(); // 应用新时间需要重置
}

/* 辅助功能 */
// 按钮状态管理
function toggleButtonStates(running) {
  startBtn.disabled = running;
  stopBtn.disabled = !running;
  resetBtn.disabled = running;
}

// 全屏切换
function toggleFullscreen() {
  if (!document.fullscreenElement) {
    document.documentElement.requestFullscreen();
  } else {
    document.exitFullscreen();
  }
}

// 播放提示音
function playAlert() {
  const audio = new Audio('data:audio/wav;base64,');
  audio.play();
}

// 全屏状态同步
document.addEventListener('fullscreenchange', () => {
  fsButton.textContent = document.fullscreenElement ? '返回' : '全屏';
});